record.field:
user_model = env['res.users']
existing_user = user_model.search([('login', '=', record.email)], limit=1)
if not existing_user:
    user_model.create({
        'name': record.name,
        'login': record.email,
        'email': record.email,
        'groups_id': [(6, 0, [env.ref('base.group_user').id])],
    })
